import { useState } from "react";
interface Props {
  poste: String[];
  heading: String;
  onselectpost: ({ post: String }) => String;
}
//import { MouseEvent } from "react";
//<button type="button" className="btn btn-primary" onClick={()=> setselect(index)}>Primary</button>

function List({ poste, heading,onselectpost}: Props) {
  const [select, setselect] = useState(-2);

  return (
    <>
      <h1>{heading}</h1>
      <ul class="list-group">
        {poste.map((post, index) => (
          <li
            class={
              select === index ? "list-group-item active" : "list-group-item "
            }
            key={post}
            onClick={() => {
              setselect(index);
              onselectpost(post);
            }}
          >
            {post}
          </li>
        ))}
      </ul>
    </>
  );
}

export default List;
