import { useState } from "react";
import React from "react";

const Todo = () => {
  const [name, setname] = useState("");
  const [isvalid, setisvalid] = useState(false);
  const [newvalue, setnewvalue] = useState([""]);
  const handlefonc = (event) => {
    setname(event.target.value);
    setnewvalue((current) => [...current,event.target.value]);
  };
  const deletehistory=(index)=>{
    setnewvalue((current)=>{
        current.slice(index,1);
        return [ ...current];
    })
  }
  return (
    <div>
      <input
        placeholder="name"
        type="text"
        value={name}
        onChange={handlefonc}
      />
      <input
        type="checkbox"
        checked={isvalid}
        onChange={() => setisvalid((prev) => !prev)}
      />
      <p>{isvalid && "papa "}</p>
      <p>{name ? `Hello ${name}` : "Write your name"}</p>
      <ul class="list-group">
        {newvalue.map((value, index) => (
          <li onClick={()=> deletehistory(index)} class="list-group-item" key={index}>
            {value}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Todo;
