import { useState } from "react";
import React from "react";

const Todo = () => {
  const [name, setname] = useState("");
  const [isvalid, setisvalid] = useState(false);
  const handlefonc = (event) => {
    setname(event.target.value);
  };
  return (
    <div>
      <input
        placeholder="name"
        type="text"
        value={name}
        onChange={handlefonc}
      />
      <input
        type="checkbox"
        checked={isvalid}
        onChange={() => setisvalid((prev) => !prev)}
      />
      <p>{isvalid && 'papa '}</p>
      <p>{name ? `Hello ${name}` : "Write your name"}</p>
    </div>
  );
};

export default Todo;
