import { useState } from "react";
//import { MouseEvent } from "react";
//<button type="button" className="btn btn-primary" onClick={()=> setselect(index)}>Primary</button>
const [select, setselect] = useState(-2);

function List() {

  const poste = ["toto","tomate","oigons"];
  return (
    <>
    
      <ul class="list-group">
       {poste.map((post,index) => <li
        class={ select === index ? "list-group-item" :  "list-group-item active"}
        key ={post}
        onClick={()=> setselect(index)}>{post}</li> )}
        
      </ul>
    </>
  );
}

export default List;
