import "./App.css";
import React from "react";
import { useState } from "react";

function App() {
  return (
    <div>
     <TodoForm/>
      <Todo>toto</Todo>
    </div>
  );
}

const TodoForm = (props) => {
  const [insert, setinsert] = useState([""]);

  const handlesubmit = (event) => {
    event.preventDefault();

    /*µprops.onSubmit({
      id: Math.floor(Math.random()*10000),
      text: insert
    });*/
    const addtodo = event.currentTarget.elements.ado.value;
    console.log(addtodo);
    setinsert("");
  };

  const handlechange = (event) => {
    setinsert(event.target.value);
  };
  return (
    <div>
      <form onSubmit={handlesubmit}>
        <input id="ado" type="text" value={insert} onChange={handlechange} />
        <button>submit</button>
      </form>
    </div>
  );
};



const Todo = ({children}:Props) => {
  return (
    <ul className="list-group">
      <li className="list-group-item">
        <input
          className="form-check-input me-1"
          type="checkbox"
          value=""
          id="firstCheckboxStretched"
        />
        <label
          className="form-check-label stretched-link"
          for="firstCheckboxStretched"
        >
          {children}
        </label>
      </li>
    </ul>
  );
};

export default App;
